648     if (_glfw.ns.eventSource)
649     {
650         CFRelease(_glfw.ns.eventSource);
651         _glfw.ns.eventSource = NULL;
652     }
653 
654     if (_glfw.ns.delegate)
655     {
656         [NSApp setDelegate:nil];
657         [_glfw.ns.delegate release];
658         _glfw.ns.delegate = nil;
659     }
660 
661     if (_glfw.ns.helper)
662     {
663         [[NSNotificationCenter defaultCenter]
664             removeObserver:_glfw.ns.helper
665                       name:NSTextInputContextKeyboardSelectionDidChangeNotification
666                     object:nil];
667         [[NSNotificationCenter defaultCenter]
668             removeObserver:_glfw.ns.helper];
669         [_glfw.ns.helper release];
670         _glfw.ns.helper = nil;
671     }
672 
673     if (_glfw.ns.keyUpMonitor)
674         [NSEvent removeMonitor:_glfw.ns.keyUpMonitor];
675 
676     _glfw_free(_glfw.ns.clipboardString);
677 
678     _glfwTerminateNSGL();
679 
680     } // autoreleasepool
681 }

889 {
890     return CGDisplayBounds(CGMainDisplayID()).size.height - y - 1;
891 }
