section.programlisting:
207 {
208     if (count && size)
209     {
210         void* block;
211 
212         if (count > SIZE_MAX / size)
213         {
214             _glfwInputError(GLFW_INVALID_VALUE, "Allocation size overflow");
215             return NULL;
216         }
217 
218         block = _glfw.allocator.allocate(count * size, _glfw.allocator.user);
219         if (block)
220             return memset(block, 0, count * size);
221         else
222         {
223             _glfwInputError(GLFW_OUT_OF_MEMORY, NULL);
224             return NULL;
225         }
226     }
227     else
228         return NULL;
229 }

195 {
196     if (a != a)
197         return b;
198     else if (b != b)
199         return a;
200     else if (a > b)
201         return a;
202     else
203         return b;
204 }

183 {
184     if (a != a)
185         return b;
186     else if (b != b)
187         return a;
188     else if (a < b)
189         return a;
190     else
191         return b;
192 }

232 {
233     if (block && size)
234     {
235         void* resized = _glfw.allocator.reallocate(block, size, _glfw.allocator.user);
236         if (resized)
237             return resized;
238         else
239         {
240             _glfwInputError(GLFW_OUT_OF_MEMORY, NULL);
241             return NULL;
242         }
243     }
244     else if (block)
245     {
246         _glfw_free(block);
247         return NULL;
248     }
249     else
250         return _glfw_calloc(1, size);
251 }

175 {
176     const size_t length = strlen(source);
177     char* result = _glfw_calloc(length + 1, 1);
178     strcpy(result, source);
179     return result;
180 }

198 {
199     ramp->red = _glfw_calloc(size, sizeof(unsigned short));
200     ramp->green = _glfw_calloc(size, sizeof(unsigned short));
201     ramp->blue = _glfw_calloc(size, sizeof(unsigned short));
202     ramp->size = size;
203 }

439 {
440     int jid;
441     _GLFWjoystick* js;
442 
443     for (jid = 0;  jid <= GLFW_JOYSTICK_LAST;  jid++)
444     {
445         if (!_glfw.joysticks[jid].present)
446             break;
447     }
448 
449     if (jid > GLFW_JOYSTICK_LAST)
450         return NULL;
451 
452     js = _glfw.joysticks + jid;
453     js->present     = GLFW_TRUE;
454     js->axes        = _glfw_calloc(axisCount, sizeof(float));
455     js->buttons     = _glfw_calloc(buttonCount + (size_t) hatCount * 4, 1);
456     js->hats        = _glfw_calloc(hatCount, 1);
457     js->axisCount   = axisCount;
458     js->buttonCount = buttonCount;
459     js->hatCount    = hatCount;
460 
461     strncpy(js->name, name, sizeof(js->name) - 1);
462     strncpy(js->guid, guid, sizeof(js->guid) - 1);
463     js->mapping = findValidMapping(js);
464 
465     return js;
466 }

169 {
170     _GLFWmonitor* monitor = _glfw_calloc(1, sizeof(_GLFWmonitor));
171     monitor->widthMM = widthMM;
172     monitor->heightMM = heightMM;
173 
174     strncpy(monitor->name, name, sizeof(monitor->name) - 1);
175 
176     return monitor;
177 }

181 {
182     unsigned int i;
183     unsigned int missing, leastMissing = UINT_MAX;
184     unsigned int colorDiff, leastColorDiff = UINT_MAX;
185     unsigned int extraDiff, leastExtraDiff = UINT_MAX;
186     const _GLFWfbconfig* current;
187     const _GLFWfbconfig* closest = NULL;
188 
189     for (i = 0;  i < count;  i++)
190     {
191         current = alternatives + i;
192 
193         if (desired->stereo > 0 && current->stereo == 0)
194         {
195             // Stereo is a hard constraint
196             continue;
197         }
198 
199         // Count number of missing buffers
200         {
201             missing = 0;
202 
203             if (desired->alphaBits > 0 && current->alphaBits == 0)
204                 missing++;
205 
206             if (desired->depthBits > 0 && current->depthBits == 0)
207                 missing++;
208 
209             if (desired->stencilBits > 0 && current->stencilBits == 0)
210                 missing++;
211 
212             if (desired->auxBuffers > 0 &&
213                 current->auxBuffers < desired->auxBuffers)
214             {
215                 missing += desired->auxBuffers - current->auxBuffers;
216             }
217 
218             if (desired->samples > 0 && current->samples == 0)
219             {
220                 // Technically, several multisampling buffers could be
221                 // involved, but that's a lower level implementation detail and
222                 // not important to us here, so we count them as one
223                 missing++;
224             }
225 
226             if (desired->transparent != current->transparent)
227                 missing++;
228         }
229 
230         // These polynomials make many small channel size differences matter
231         // less than one large channel size difference
232 
233         // Calculate color channel size difference value
234         {
235             colorDiff = 0;
236 
237             if (desired->redBits != GLFW_DONT_CARE)
238             {
239                 colorDiff += (desired->redBits - current->redBits) *
240                              (desired->redBits - current->redBits);
241             }
242 
243             if (desired->greenBits != GLFW_DONT_CARE)
244             {
245                 colorDiff += (desired->greenBits - current->greenBits) *
246                              (desired->greenBits - current->greenBits);
247             }
248 
249             if (desired->blueBits != GLFW_DONT_CARE)
250             {
251                 colorDiff += (desired->blueBits - current->blueBits) *
252                              (desired->blueBits - current->blueBits);
253             }
254         }
255 
256         // Calculate non-color channel size difference value
257         {
258             extraDiff = 0;
259 
260             if (desired->alphaBits != GLFW_DONT_CARE)
261             {
262                 extraDiff += (desired->alphaBits - current->alphaBits) *
263                              (desired->alphaBits - current->alphaBits);
264             }
265 
266             if (desired->depthBits != GLFW_DONT_CARE)
267             {
268                 extraDiff += (desired->depthBits - current->depthBits) *
269                              (desired->depthBits - current->depthBits);
270             }
271 
272             if (desired->stencilBits != GLFW_DONT_CARE)
273             {
274                 extraDiff += (desired->stencilBits - current->stencilBits) *
275                              (desired->stencilBits - current->stencilBits);
276             }
277 
278             if (desired->accumRedBits != GLFW_DONT_CARE)
279             {
280                 extraDiff += (desired->accumRedBits - current->accumRedBits) *
281                              (desired->accumRedBits - current->accumRedBits);
282             }
283 
284             if (desired->accumGreenBits != GLFW_DONT_CARE)
285             {
286                 extraDiff += (desired->accumGreenBits - current->accumGreenBits) *
287                              (desired->accumGreenBits - current->accumGreenBits);
288             }
289 
290             if (desired->accumBlueBits != GLFW_DONT_CARE)
291             {
292                 extraDiff += (desired->accumBlueBits - current->accumBlueBits) *
293                              (desired->accumBlueBits - current->accumBlueBits);
294             }
295 
296             if (desired->accumAlphaBits != GLFW_DONT_CARE)
297             {
298                 extraDiff += (desired->accumAlphaBits - current->accumAlphaBits) *
299                              (desired->accumAlphaBits - current->accumAlphaBits);
300             }
301 
302             if (desired->samples != GLFW_DONT_CARE)
303             {
304                 extraDiff += (desired->samples - current->samples) *
305                              (desired->samples - current->samples);
306             }
307 
308             if (desired->sRGB && !current->sRGB)
309                 extraDiff++;
310         }
311 
312         // Figure out if the current one is better than the best one found so far
313         // Least number of missing buffers is the most important heuristic,
314         // then color buffer size match and lastly size match for other buffers
315 
316         if (missing < leastMissing)
317             closest = current;
318         else if (missing == leastMissing)
319         {
320             if ((colorDiff < leastColorDiff) ||
321                 (colorDiff == leastColorDiff && extraDiff < leastExtraDiff))
322             {
323                 closest = current;
324             }
325         }
326 
327         if (current == closest)
328         {
329             leastMissing = missing;
330             leastColorDiff = colorDiff;
331             leastExtraDiff = extraDiff;
332         }
333     }
334 
335     return closest;
336 }

220 {
221     int i;
222     unsigned int sizeDiff, leastSizeDiff = UINT_MAX;
223     unsigned int rateDiff, leastRateDiff = UINT_MAX;
224     unsigned int colorDiff, leastColorDiff = UINT_MAX;
225     const GLFWvidmode* current;
226     const GLFWvidmode* closest = NULL;
227 
228     if (!refreshVideoModes(monitor))
229         return NULL;
230 
231     for (i = 0;  i < monitor->modeCount;  i++)
232     {
233         current = monitor->modes + i;
234 
235         colorDiff = 0;
236 
237         if (desired->redBits != GLFW_DONT_CARE)
238             colorDiff += abs(current->redBits - desired->redBits);
239         if (desired->greenBits != GLFW_DONT_CARE)
240             colorDiff += abs(current->greenBits - desired->greenBits);
241         if (desired->blueBits != GLFW_DONT_CARE)
242             colorDiff += abs(current->blueBits - desired->blueBits);
243 
244         sizeDiff = abs((current->width - desired->width) *
245                        (current->width - desired->width) +
246                        (current->height - desired->height) *
247                        (current->height - desired->height));
248 
249         if (desired->refreshRate != GLFW_DONT_CARE)
250             rateDiff = abs(current->refreshRate - desired->refreshRate);
251         else
252             rateDiff = UINT_MAX - current->refreshRate;
253 
254         if ((colorDiff < leastColorDiff) ||
255             (colorDiff == leastColorDiff && sizeDiff < leastSizeDiff) ||
256             (colorDiff == leastColorDiff && sizeDiff == leastSizeDiff && rateDiff < leastRateDiff))
257         {
258             closest = current;
259             leastSizeDiff = sizeDiff;
260             leastRateDiff = rateDiff;
261             leastColorDiff = colorDiff;
262         }
263     }
264 
265     return closest;
266 }

521 {
522     EGLint attribs[40];
523     EGLConfig config;
524     EGLContext share = NULL;
525     EGLNativeWindowType native;
526     int index = 0;
527 
528     if (!_glfw.egl.display)
529     {
530         _glfwInputError(GLFW_API_UNAVAILABLE, "EGL: API not available");
531         return GLFW_FALSE;
532     }
533 
534     if (ctxconfig->share)
535         share = ctxconfig->share->context.egl.handle;
536 
537     if (!chooseEGLConfig(ctxconfig, fbconfig, &config))
538     {
539         _glfwInputError(GLFW_FORMAT_UNAVAILABLE,
540                         "EGL: Failed to find a suitable EGLConfig");
541         return GLFW_FALSE;
542     }
543 
544     if (ctxconfig->client == GLFW_OPENGL_ES_API)
545     {
546         if (!eglBindAPI(EGL_OPENGL_ES_API))
547         {
548             _glfwInputError(GLFW_API_UNAVAILABLE,
549                             "EGL: Failed to bind OpenGL ES: %s",
550                             getEGLErrorString(eglGetError()));
551             return GLFW_FALSE;
552         }
553     }
554     else
555     {
556         if (!eglBindAPI(EGL_OPENGL_API))
557         {
558             _glfwInputError(GLFW_API_UNAVAILABLE,
559                             "EGL: Failed to bind OpenGL: %s",
560                             getEGLErrorString(eglGetError()));
561             return GLFW_FALSE;
562         }
563     }
564 
565     if (_glfw.egl.KHR_create_context)
566     {
567         int mask = 0, flags = 0;
568 
569         if (ctxconfig->client == GLFW_OPENGL_API)
570         {
571             if (ctxconfig->forward)
572                 flags |= EGL_CONTEXT_OPENGL_FORWARD_COMPATIBLE_BIT_KHR;
573 
574             if (ctxconfig->profile == GLFW_OPENGL_CORE_PROFILE)
575                 mask |= EGL_CONTEXT_OPENGL_CORE_PROFILE_BIT_KHR;
576             else if (ctxconfig->profile == GLFW_OPENGL_COMPAT_PROFILE)
577                 mask |= EGL_CONTEXT_OPENGL_COMPATIBILITY_PROFILE_BIT_KHR;
578         }
579 
580         if (ctxconfig->debug)
581             flags |= EGL_CONTEXT_OPENGL_DEBUG_BIT_KHR;
582 
583         if (ctxconfig->robustness)
584         {
585             if (ctxconfig->robustness == GLFW_NO_RESET_NOTIFICATION)
586             {
587                 setAttrib(EGL_CONTEXT_OPENGL_RESET_NOTIFICATION_STRATEGY_KHR,
588                           EGL_NO_RESET_NOTIFICATION_KHR);
589             }
590             else if (ctxconfig->robustness == GLFW_LOSE_CONTEXT_ON_RESET)
591             {
592                 setAttrib(EGL_CONTEXT_OPENGL_RESET_NOTIFICATION_STRATEGY_KHR,
593                           EGL_LOSE_CONTEXT_ON_RESET_KHR);
594             }
595 
596             flags |= EGL_CONTEXT_OPENGL_ROBUST_ACCESS_BIT_KHR;
597         }
598 
599         if (ctxconfig->noerror)
600         {
601             if (_glfw.egl.KHR_create_context_no_error)
602                 setAttrib(EGL_CONTEXT_OPENGL_NO_ERROR_KHR, GLFW_TRUE);
603         }
604 
605         if (ctxconfig->major != 1 || ctxconfig->minor != 0)
606         {
607             setAttrib(EGL_CONTEXT_MAJOR_VERSION_KHR, ctxconfig->major);
608             setAttrib(EGL_CONTEXT_MINOR_VERSION_KHR, ctxconfig->minor);
609         }
610 
611         if (mask)
612             setAttrib(EGL_CONTEXT_OPENGL_PROFILE_MASK_KHR, mask);
613 
614         if (flags)
615             setAttrib(EGL_CONTEXT_FLAGS_KHR, flags);
616     }
617     else
618     {
619         if (ctxconfig->client == GLFW_OPENGL_ES_API)
620             setAttrib(EGL_CONTEXT_CLIENT_VERSION, ctxconfig->major);
621     }
622 
623     if (_glfw.egl.KHR_context_flush_control)
624     {
625         if (ctxconfig->release == GLFW_RELEASE_BEHAVIOR_NONE)
626         {
627             setAttrib(EGL_CONTEXT_RELEASE_BEHAVIOR_KHR,
628                       EGL_CONTEXT_RELEASE_BEHAVIOR_NONE_KHR);
629         }
630         else if (ctxconfig->release == GLFW_RELEASE_BEHAVIOR_FLUSH)
631         {
632             setAttrib(EGL_CONTEXT_RELEASE_BEHAVIOR_KHR,
633                       EGL_CONTEXT_RELEASE_BEHAVIOR_FLUSH_KHR);
634         }
635     }
636 
637     setAttrib(EGL_NONE, EGL_NONE);
638 
639     window->context.egl.handle = eglCreateContext(_glfw.egl.display,
640                                                   config, share, attribs);
641 
642     if (window->context.egl.handle == EGL_NO_CONTEXT)
643     {
644         _glfwInputError(GLFW_VERSION_UNAVAILABLE,
645                         "EGL: Failed to create context: %s",
646                         getEGLErrorString(eglGetError()));
647         return GLFW_FALSE;
648     }
649 
650     // Set up attributes for surface creation
651     index = 0;
652 
653     if (fbconfig->sRGB)
654     {
655         if (_glfw.egl.KHR_gl_colorspace)
656             setAttrib(EGL_GL_COLORSPACE_KHR, EGL_GL_COLORSPACE_SRGB_KHR);
657     }
658 
659     if (!fbconfig->doublebuffer)
660         setAttrib(EGL_RENDER_BUFFER, EGL_SINGLE_BUFFER);
661 
662     if (_glfw.egl.EXT_present_opaque)
663         setAttrib(EGL_PRESENT_OPAQUE_EXT, !fbconfig->transparent);
664 
665     setAttrib(EGL_NONE, EGL_NONE);
666 
667     native = _glfw.platform.getEGLNativeWindow(window);
668     // HACK: ANGLE does not implement eglCreatePlatformWindowSurfaceEXT
669     //       despite reporting EGL_EXT_platform_base
670     if (_glfw.egl.platform && _glfw.egl.platform != EGL_PLATFORM_ANGLE_ANGLE)
671     {
672         window->context.egl.surface =
673             eglCreatePlatformWindowSurfaceEXT(_glfw.egl.display, config, native, attribs);
674     }
675     else
676     {
677         window->context.egl.surface =
678             eglCreateWindowSurface(_glfw.egl.display, config, native, attribs);
679     }
680 
681     if (window->context.egl.surface == EGL_NO_SURFACE)
682     {
683         _glfwInputError(GLFW_PLATFORM_ERROR,
684                         "EGL: Failed to create window surface: %s",
685                         getEGLErrorString(eglGetError()));
686         return GLFW_FALSE;
687     }
688 
689     window->context.egl.config = config;
690 
691     // Load the appropriate client library
692     if (!_glfw.egl.KHR_get_all_proc_addresses)
693     {
694         int i;
695         const char** sonames;
696         const char* es1sonames[] =
697         {
698 #if defined(_GLFW_GLESV1_LIBRARY)
699             _GLFW_GLESV1_LIBRARY,
700 #elif defined(_GLFW_WIN32)
701             "GLESv1_CM.dll",
702             "libGLES_CM.dll",
703 #elif defined(_GLFW_COCOA)
704             "libGLESv1_CM.dylib",
705 #elif defined(__OpenBSD__)
706             "libGLESv1_CM.so",
707 #else
708             "libGLESv1_CM.so.1",
709             "libGLES_CM.so.1",
710 #endif
711             NULL
712         };
713         const char* es2sonames[] =
714         {
715 #if defined(_GLFW_GLESV2_LIBRARY)
716             _GLFW_GLESV2_LIBRARY,
717 #elif defined(_GLFW_WIN32)
718             "GLESv2.dll",
719             "libGLESv2.dll",
720 #elif defined(_GLFW_COCOA)
721             "libGLESv2.dylib",
722 #elif defined(__CYGWIN__)
723             "libGLESv2-2.so",
724 #elif defined(__OpenBSD__)
725             "libGLESv2.so",
726 #else
727             "libGLESv2.so.2",
728 #endif
729             NULL
730         };
731         const char* glsonames[] =
732         {
733 #if defined(_GLFW_OPENGL_LIBRARY)
734             _GLFW_OPENGL_LIBRARY,
735 #elif defined(_GLFW_WIN32)
736 #elif defined(_GLFW_COCOA)
737 #elif defined(__OpenBSD__)
738             "libGL.so",
739 #else
740             "libGL.so.1",
741 #endif
742             NULL
743         };
744 
745         if (ctxconfig->client == GLFW_OPENGL_ES_API)
746         {
747             if (ctxconfig->major == 1)
748                 sonames = es1sonames;
749             else
750                 sonames = es2sonames;
751         }
752         else
753             sonames = glsonames;
754 
755         for (i = 0;  sonames[i];  i++)
756         {
757             // HACK: Match presence of lib prefix to increase chance of finding
758             //       a matching pair in the jungle that is Win32 EGL/GLES
759             if (_glfw.egl.prefix != (strncmp(sonames[i], "lib", 3) == 0))
760                 continue;
761 
762             window->context.egl.client = _glfwPlatformLoadModule(sonames[i]);
763             if (window->context.egl.client)
764                 break;
765         }
766 
767         if (!window->context.egl.client)
768         {
769             _glfwInputError(GLFW_API_UNAVAILABLE,
770                             "EGL: Failed to load client library");
771             return GLFW_FALSE;
772         }
773     }
774 
775     window->context.makeCurrent = makeContextCurrentEGL;
776     window->context.swapBuffers = swapBuffersEGL;
777     window->context.swapInterval = swapIntervalEGL;
778     window->context.extensionSupported = extensionSupportedEGL;
779     window->context.getProcAddress = getProcAddressEGL;
780     window->context.destroy = destroyContextEGL;
781 
782     return GLFW_TRUE;
783 }

203 {
204     OSMesaContext share = NULL;
205     const int accumBits = fbconfig->accumRedBits +
206                           fbconfig->accumGreenBits +
207                           fbconfig->accumBlueBits +
208                           fbconfig->accumAlphaBits;
209 
210     if (ctxconfig->client == GLFW_OPENGL_ES_API)
211     {
212         _glfwInputError(GLFW_API_UNAVAILABLE,
213                         "OSMesa: OpenGL ES is not available on OSMesa");
214         return GLFW_FALSE;
215     }
216 
217     if (ctxconfig->share)
218         share = ctxconfig->share->context.osmesa.handle;
219 
220     if (OSMesaCreateContextAttribs)
221     {
222         int index = 0, attribs[40];
223 
224         setAttrib(OSMESA_FORMAT, OSMESA_RGBA);
225         setAttrib(OSMESA_DEPTH_BITS, fbconfig->depthBits);
226         setAttrib(OSMESA_STENCIL_BITS, fbconfig->stencilBits);
227         setAttrib(OSMESA_ACCUM_BITS, accumBits);
228 
229         if (ctxconfig->profile == GLFW_OPENGL_CORE_PROFILE)
230         {
231             setAttrib(OSMESA_PROFILE, OSMESA_CORE_PROFILE);
232         }
233         else if (ctxconfig->profile == GLFW_OPENGL_COMPAT_PROFILE)
234         {
235             setAttrib(OSMESA_PROFILE, OSMESA_COMPAT_PROFILE);
236         }
237 
238         if (ctxconfig->major != 1 || ctxconfig->minor != 0)
239         {
240             setAttrib(OSMESA_CONTEXT_MAJOR_VERSION, ctxconfig->major);
241             setAttrib(OSMESA_CONTEXT_MINOR_VERSION, ctxconfig->minor);
242         }
243 
244         if (ctxconfig->forward)
245         {
246             _glfwInputError(GLFW_VERSION_UNAVAILABLE,
247                             "OSMesa: Forward-compatible contexts not supported");
248             return GLFW_FALSE;
249         }
250 
251         setAttrib(0, 0);
252 
253         window->context.osmesa.handle =
254             OSMesaCreateContextAttribs(attribs, share);
255     }
256     else
257     {
258         if (ctxconfig->profile)
259         {
260             _glfwInputError(GLFW_VERSION_UNAVAILABLE,
261                             "OSMesa: OpenGL profiles unavailable");
262             return GLFW_FALSE;
263         }
264 
265         window->context.osmesa.handle =
266             OSMesaCreateContextExt(OSMESA_RGBA,
267                                    fbconfig->depthBits,
268                                    fbconfig->stencilBits,
269                                    accumBits,
270                                    share);
271     }
272 
273     if (window->context.osmesa.handle == NULL)
274     {
275         _glfwInputError(GLFW_VERSION_UNAVAILABLE,
276                         "OSMesa: Failed to create context");
277         return GLFW_FALSE;
278     }
279 
280     window->context.makeCurrent = makeContextCurrentOSMesa;
281     window->context.swapBuffers = swapBuffersOSMesa;
282     window->context.swapInterval = swapIntervalOSMesa;
283     window->context.extensionSupported = extensionSupportedOSMesa;
284     window->context.getProcAddress = getProcAddressOSMesa;
285     window->context.destroy = destroyContextOSMesa;
286 
287     return GLFW_TRUE;
288 }

147 {
148     size_t count = 0;
149 
150     if (codepoint < 0x80)
151         s[count++] = (char) codepoint;
152     else if (codepoint < 0x800)
153     {
154         s[count++] = (codepoint >> 6) | 0xc0;
155         s[count++] = (codepoint & 0x3f) | 0x80;
156     }
157     else if (codepoint < 0x10000)
158     {
159         s[count++] = (codepoint >> 12) | 0xe0;
160         s[count++] = ((codepoint >> 6) & 0x3f) | 0x80;
161         s[count++] = (codepoint & 0x3f) | 0x80;
162     }
163     else if (codepoint < 0x110000)
164     {
165         s[count++] = (codepoint >> 18) | 0xf0;
166         s[count++] = ((codepoint >> 12) & 0x3f) | 0x80;
167         s[count++] = ((codepoint >> 6) & 0x3f) | 0x80;
168         s[count++] = (codepoint & 0x3f) | 0x80;
169     }
170 
171     return count;
172 }

165 {
166     switch (result)
167     {
168         case VK_SUCCESS:
169             return "Success";
170         case VK_NOT_READY:
171             return "A fence or query has not yet completed";
172         case VK_TIMEOUT:
173             return "A wait operation has not completed in the specified time";
174         case VK_EVENT_SET:
175             return "An event is signaled";
176         case VK_EVENT_RESET:
177             return "An event is unsignaled";
178         case VK_INCOMPLETE:
179             return "A return array was too small for the result";
180         case VK_ERROR_OUT_OF_HOST_MEMORY:
181             return "A host memory allocation has failed";
182         case VK_ERROR_OUT_OF_DEVICE_MEMORY:
183             return "A device memory allocation has failed";
184         case VK_ERROR_INITIALIZATION_FAILED:
185             return "Initialization of an object could not be completed for implementation-specific reasons";
186         case VK_ERROR_DEVICE_LOST:
187             return "The logical or physical device has been lost";
188         case VK_ERROR_MEMORY_MAP_FAILED:
189             return "Mapping of a memory object has failed";
190         case VK_ERROR_LAYER_NOT_PRESENT:
191             return "A requested layer is not present or could not be loaded";
192         case VK_ERROR_EXTENSION_NOT_PRESENT:
193             return "A requested extension is not supported";
194         case VK_ERROR_FEATURE_NOT_PRESENT:
195             return "A requested feature is not supported";
196         case VK_ERROR_INCOMPATIBLE_DRIVER:
197             return "The requested version of Vulkan is not supported by the driver or is otherwise incompatible";
198         case VK_ERROR_TOO_MANY_OBJECTS:
199             return "Too many objects of the type have already been created";
200         case VK_ERROR_FORMAT_NOT_SUPPORTED:
201             return "A requested format is not supported on this device";
202         case VK_ERROR_SURFACE_LOST_KHR:
203             return "A surface is no longer available";
204         case VK_SUBOPTIMAL_KHR:
205             return "A swapchain no longer matches the surface properties exactly, but can still be used";
206         case VK_ERROR_OUT_OF_DATE_KHR:
207             return "A surface has changed in such a way that it is no longer compatible with the swapchain";
208         case VK_ERROR_INCOMPATIBLE_DISPLAY_KHR:
209             return "The display used by a swapchain does not use the same presentable image layout";
210         case VK_ERROR_NATIVE_WINDOW_IN_USE_KHR:
211             return "The requested window is already connected to a VkSurfaceKHR, or to some other non-Vulkan API";
212         case VK_ERROR_VALIDATION_FAILED_EXT:
213             return "A validation layer found an error";
214         default:
215             return "ERROR: UNKNOWN VULKAN ERROR";
216     }
217 }

