399                          {
400   /*
401    sin(theta / 2) = length(x*x + y*y + z*z)
402    cos(theta / 2) = w
403    theta          = 2 * atan(sin(theta / 2) / cos(theta / 2))
404    */
405   return 2.0f * atan2f(glm_quat_imaglen(q), glm_quat_real(q));
406 }

