199                                                 {
200   CGLM_ALIGN(8) vec3 axis;
201   float cos_theta;
202   float cos_half_theta;
203 
204   cos_theta = glm_vec3_dot(a, b);
205   if (cos_theta >= 1.f - GLM_FLT_EPSILON) {  /*  a ∥ b  */
206     glm_quat_identity(dest);
207     return;
208   }
209   if (cos_theta < -1.f + GLM_FLT_EPSILON) {  /*  angle(a, b) = π  */
210     glm_vec3_ortho(a, axis);
211     cos_half_theta = 0.f;                    /*  cos π/2 */
212   } else {
213     glm_vec3_cross(a, b, axis);
214     cos_half_theta = 1.0f + cos_theta;       /*  cos 0 + cos θ  */
215   }
216 
217   glm_quat_init(dest, axis[0], axis[1], axis[2], cos_half_theta);
218   glm_quat_normalize(dest);
219 }

