555                                    {
556   float w, x, y, z,
557         xx, yy, zz,
558         xy, yz, xz,
559         wx, wy, wz, norm, s;
560 
561   norm = glm_quat_norm(q);
562   s    = norm > 0.0f ? 2.0f / norm : 0.0f;
563 
564   x = q[0];
565   y = q[1];
566   z = q[2];
567   w = q[3];
568 
569   xx = s * x * x;   xy = s * x * y;   wx = s * w * x;
570   yy = s * y * y;   yz = s * y * z;   wy = s * w * y;
571   zz = s * z * z;   xz = s * x * z;   wz = s * w * z;
572 
573   dest[0][0] = 1.0f - yy - zz;
574   dest[1][1] = 1.0f - xx - zz;
575   dest[2][2] = 1.0f - xx - yy;
576 
577   dest[0][1] = xy + wz;
578   dest[1][2] = yz + wx;
579   dest[2][0] = xz + wy;
580 
581   dest[1][0] = xy - wz;
582   dest[2][1] = yz - wx;
583   dest[0][2] = xz - wy;
584 }

