240                                              {
241 #if defined( __SSE2__ ) || defined( __SSE2__ )
242   __m128 xdot, x0;
243   float  dot;
244 
245   x0   = glmm_load(q);
246   xdot = glmm_vdot(x0, x0);
247   dot  = _mm_cvtss_f32(xdot);
248 
249   if (dot <= 0.0f) {
250     glm_quat_identity(dest);
251     return;
252   }
253 
254   glmm_store(dest, _mm_div_ps(x0, _mm_sqrt_ps(xdot)));
255 #else
256   float dot;
257 
258   dot = glm_vec4_norm2(q);
259 
260   if (dot <= 0.0f) {
261     glm_quat_identity(dest);
262     return;
263   }
264 
265   glm_vec4_scale(q, 1.0f / sqrtf(dot), dest);
266 #endif
267 }

