790                                               {
791   CGLM_ALIGN(16) versor p;
792   CGLM_ALIGN(8)  vec3   u, v1, v2;
793   float s;
794 
795   glm_quat_normalize_to(q, p);
796   glm_quat_imag(p, u);
797   s = glm_quat_real(p);
798 
799   glm_vec3_scale(u, 2.0f * glm_vec3_dot(u, v), v1);
800   glm_vec3_scale(v, s * s - glm_vec3_dot(u, u), v2);
801   glm_vec3_add(v1, v2, v1);
802 
803   glm_vec3_cross(u, v, v2);
804   glm_vec3_scale(v2, 2.0f * s, v2);
805 
806   glm_vec3_add(v1, v2, dest);
807 }

689                                                              {
690   CGLM_ALIGN(16) vec4 q1, q2;
691   float cosTheta, sinTheta, angle;
692 
693   cosTheta = glm_quat_dot(from, to);
694   glm_quat_copy(from, q1);
695 
696   if (fabsf(cosTheta) >= 1.0f) {
697     glm_quat_copy(q1, dest);
698     return;
699   }
700 
701   if (cosTheta < 0.0f) {
702     glm_vec4_negate(q1);
703     cosTheta = -cosTheta;
704   }
705 
706   sinTheta = sqrtf(1.0f - cosTheta * cosTheta);
707 
708   /* LERP to avoid zero division */
709   if (fabsf(sinTheta) < 0.001f) {
710     glm_quat_lerp(from, to, t, dest);
711     return;
712   }
713 
714   /* SLERP */
715   angle = acosf(cosTheta);
716   glm_vec4_scale(q1, sinf((1.0f - t) * angle), q1);
717   glm_vec4_scale(to, sinf(t * angle), q2);
718 
719   glm_vec4_add(q1, q2, q1);
720   glm_vec4_scale(q1, 1.0f / sinTheta, dest);
721 }
