248                                 {
249   mat3 m1 = GLM_MAT3_IDENTITY_INIT;
250   vec3 v1 = {0.0f, 1.0f, 1.0f}, v2 = {0.0f, 1.0f, 1.0f};
251 
252   GLM(rotate2d)(m1, GLM_PI_2f);
253   glm_mat3_mulv(m1, v1, v1);
254 
255   ASSERT(test_eq(v1[0], -1.0f))
256   ASSERT(test_eq(v1[1],  0.0f))
257   ASSERT(test_eq(v1[2],  1.0f))
258   
259   glm_vec3_copy(v2, v1);
260 
261   GLM(rotate2d)(m1, GLM_PI_2f);
262   glm_mat3_mulv(m1, v1, v1);
263 
264   ASSERT(test_eq(v1[0],  0.0f))
265   ASSERT(test_eq(v1[1], -1.0f))
266   ASSERT(test_eq(v1[2],  1.0f))
267 
268   glm_vec3_copy(v2, v1);
269 
270   GLM(rotate2d)(m1, GLM_PI_2f);
271   glm_mat3_mulv(m1, v1, v1);
272 
273   ASSERT(test_eq(v1[0], 1.0f))
274   ASSERT(test_eq(v1[1], 0.0f))
275   ASSERT(test_eq(v1[2], 1.0f))
276 
277   TEST_SUCCESS
