64   glm_mat4_identity(m1);
65   GLM(translate_x)(m1, 13.0f);
66   glm_mat4_mulv(m1, v1, v2);
67 
68   ASSERT(test_eq(v2[0], 14.0f))
69   ASSERT(test_eq(v2[1], 2.0f))
70   ASSERT(test_eq(v2[2], 3.0f))
71   ASSERT(test_eq(v2[3], 1.0f))
72 
73   glm_mat4_identity(m1);
74   GLM(translate_x)(m1, -1.0f);
75   glm_mat4_mulv(m1, v2, v2);
76 
77   ASSERT(test_eq(v2[0], 13.0f))
78   ASSERT(test_eq(v2[1], 2.0f))
79   ASSERT(test_eq(v2[2], 3.0f))
80   ASSERT(test_eq(v2[3], 1.0f))
81 
82   TEST_SUCCESS
83 }

