128                                 {
129   mat4 m1 = GLM_MAT4_IDENTITY_INIT;
130   mat4 m2 = GLM_MAT4_IDENTITY_INIT;
131   mat4 m3;
132   mat4 m4 = GLM_MAT4_ZERO_INIT;
133   int  i, j, k;
134 
135   /* test random matrices */
136   /* random matrices */
137   test_rand_mat4(m1);
138   test_rand_mat4(m2);
139 
140   GLM(mat4_mul)(m1, m2, m3);
141   for (i = 0; i < 4; i++) {
142     for (j = 0; j < 4; j++) {
143       for (k = 0; k < 4; k++)
144         /* column-major */
145         m4[i][j] += m1[k][j] * m2[i][k];
146     }
147   }
148 
149   ASSERTIFY(test_assert_mat4_eq(m3, m4))
150 
151   /* test pre compiled */
152   GLM(mat4_mul)(m1, m2, m3);
153   ASSERTIFY(test_assert_mat4_eq(m3, m4))
154 
155   TEST_SUCCESS
156 }

