609   GLM(quat_mat3)(q1, m3);
610   glm_mat4_identity(m1);
611   glm_mat4_ins3(m3, m1);
612 
613   GLM(mat4_quat)(m1, q2);
614 
615   GLM(rotate_make)(m2, GLM_PI_4f, axis2);
616   GLM(mat4_quat)(m1, q3);
617 
618   GLM(quat_axis)(q3, axis1);
619 
620   GLM(vec3_normalize)(axis1);
621   GLM(vec3_normalize)(axis2);
622 
623   ASSERT(test_eq(glm_quat_angle(q3), GLM_PI_4f))
624   ASSERTIFY(test_assert_vec3_eq(axis1, axis2))
625   ASSERTIFY(test_assert_vec4_eq(q1, q2))
626   ASSERTIFY(test_assert_mat4_eq(m1, m2))
627   ASSERTIFY(test_assert_vec4_eq(q1, q3))
628 
629   /* 1. test quat to mat and mat to quat */
630   for (i = 0; i < 1000; i++) {
631     test_rand_quat(q1);
632 
