638 
639     GLM(quat_mat3)(q2, m3);
640     glm_mat4_identity(m2);
641     glm_mat4_ins3(m3, m2);
642 
643     /* 2. test first quat and generated one equality */
644     ASSERTIFY(test_assert_quat_eq_abs(q1, q2));
645 
646     /* 3. test first rot and second rotation */
647     /* almost equal */
648     ASSERTIFY(test_assert_mat4_eq2(m1, m2, 0.000009f));
649   }
650 
651   TEST_SUCCESS
652 }

