783   GLM(quat_nlerp)(q1, q2, 0.5f, q3);
784 
785   glm_quat_axis(q3, v2);
786   glm_vec3_normalize(v1);
787   glm_vec3_normalize(v2);
788 
789   ASSERT(glm_quat_angle(q3) > glm_rad(30.0f));
790   ASSERT(glm_quat_angle(q3) < glm_rad(90.0f));
791   ASSERTIFY(test_assert_vec3_eq(v1, v2))
792 
793   TEST_SUCCESS
794 }

247                                       {
248   versor v1 = {2.0f, -3.0f, 4.0f, 5.0f}, v2 = {2.0f, -3.0f, 4.0f, 5.0f};
249   float  s  = 1.0f;
250   float  norm;
251 
252   GLM(quat_normalize)(v2);
253 
254   norm = sqrtf(v1[0] * v1[0] + v1[1] * v1[1] + v1[2] * v1[2] + v1[3] * v1[3]);
255   if (norm <= 0.0f) {
256     ASSERTIFY(test_assert_quat_eq_identity(v1))
257 
258     TEST_SUCCESS
259   }
260 
261   norm = s / norm;
262 
263   ASSERT(test_eq(v1[0] * norm, v2[0]))
264   ASSERT(test_eq(v1[1] * norm, v2[1]))
265   ASSERT(test_eq(v1[2] * norm, v2[2]))
266   ASSERT(test_eq(v1[3] * norm, v2[3]))
267 
268   glm_vec4_zero(v1);
269   GLM(quat_normalize)(v1);
270   ASSERTIFY(test_assert_quat_eq_identity(v1))
271 
272   TEST_SUCCESS
273 }

