965                                    {
966   mat4   m1 = GLM_MAT4_IDENTITY_INIT, m2;
967   versor q1;
968   vec4   v1 = {1.0f, 0.0f, 0.0f, 1.0f};
969 
970   /* rotate X around Y = -Z */
971   glm_quatv(q1, GLM_PI_2f, GLM_YUP);
972   GLM(quat_rotate)(m1, q1, m1);
973   glm_rotate_make(m2, GLM_PI_2f, GLM_YUP);
974   ASSERTIFY(test_assert_mat4_eq(m1, m2))
975   glm_mat4_mulv(m1, v1, v1);
976 
977   ASSERT(test_eq(v1[0],  0.0f))
978   ASSERT(test_eq(v1[1],  0.0f))
979   ASSERT(test_eq(v1[2], -1.0f))
980 
981   glm_mat4_identity(m1);
982   glm_mat4_identity(m2);
983   
984   /* rotate -Z around X = Y */
985   glm_quatv(q1, GLM_PI_2f, GLM_XUP);
986   GLM(quat_rotate)(m1, q1, m1);
987   glm_rotate(m2, GLM_PI_2f, GLM_XUP);
988   ASSERTIFY(test_assert_mat4_eq(m1, m2))
989   glm_mat4_mulv(m1, v1, v1);
