992   ASSERT(test_eq(v1[1],  1.0f))
993   ASSERT(test_eq(v1[2],  0.0f))
994 
995   glm_mat4_identity(m1);
996   glm_mat4_identity(m2);
997 
998   /* rotate Y around X = +Z */
999   glm_quatv(q1, GLM_PI_2f, GLM_XUP);
1000   GLM(quat_rotate)(m1, q1, m1);
1001   glm_rotate(m2, GLM_PI_2f, GLM_XUP);
1002   ASSERTIFY(test_assert_mat4_eq(m1, m2))
