403                                       {
404   vec2  v1 = {2.0f, -3.0f}, v2 = {2.0f, -3.0f};
405   float s  = 1.0f;
406   float norm;
407 
408   GLM(vec2_normalize)(v2);
409 
410   norm = sqrtf(v1[0] * v1[0] + v1[1] * v1[1]);
411   if (norm == 0.0f) {
412     ASSERT(test_eq(v1[0], 0.0f))
413     ASSERT(test_eq(v1[1], 0.0f))
414 
415     TEST_SUCCESS
416   }
417   
418   norm = s / norm;
419   
420   ASSERT(test_eq(v1[0] * norm, v2[0]))
421   ASSERT(test_eq(v1[1] * norm, v2[1]))
422 
423   glm_vec2_zero(v1);
424   GLM(vec2_normalize)(v1);
425   ASSERTIFY(test_assert_vec2_eq(v1, GLM_VEC2_ZERO))
