133   glmc_vec3_flipsign(v2);
134 
135   ASSERTIFY(test_assert_vec3_eq(v1, v3))
136   ASSERTIFY(test_assert_vec3_eq(v2, v3))
137   
138   TEST_SUCCESS
139 }

