250 #if defined( __SSE__ ) || defined( __SSE2__ )
251   glmm_store(dest, glmm_abs(glmm_load(v)));
252 #elif defined(CGLM_NEON_FP)
253   vst1q_f32(dest, vabsq_f32(vld1q_f32(v)));
254 #else
