94                                    {
95   return fabsf(v[0] - val) <= GLM_FLT_EPSILON
96          && fabsf(v[1] - val) <= GLM_FLT_EPSILON
97          && fabsf(v[2] - val) <= GLM_FLT_EPSILON
98          && fabsf(v[3] - val) <= GLM_FLT_EPSILON;
