137          && fabsf(a[1] - b[1]) <= GLM_FLT_EPSILON
138          && fabsf(a[2] - b[2]) <= GLM_FLT_EPSILON
139          && fabsf(a[3] - b[3]) <= GLM_FLT_EPSILON;
