149                      {
150   float max;
151 
152   max = glm_vec3_max(v);
153   if (v[3] > max)
154     max = v[3];
155 
156   return max;
157 }

166                      {
167   float min;
168 
169   min = glm_vec3_min(v);
170   if (v[3] < min)
171     min = v[3];
172 
173   return min;
174 }

221                                  {
222 #if defined( __SSE2__ ) || defined( __SSE2__ )
223   __m128 x0, x1, x2, x3, x4;
224 
225   x0 = glmm_load(v);
226   x1 = _mm_set_ps(0.0f, 0.0f, 1.0f, -1.0f);
227   x2 = glmm_splat(x1, 2);
228 
229   x3 = _mm_and_ps(_mm_cmpgt_ps(x0, x2), glmm_splat(x1, 1));
230   x4 = _mm_and_ps(_mm_cmplt_ps(x0, x2), glmm_splat(x1, 0));
231 
232   glmm_store(dest, _mm_or_ps(x3, x4));
233 #else
234   dest[0] = glm_signf(v[0]);
235   dest[1] = glm_signf(v[1]);
236   dest[2] = glm_signf(v[2]);
237   dest[3] = glm_signf(v[3]);
238 #endif
239 }

