184     glEnableClientState(GL_COLOR_ARRAY);
185     glVertexPointer(3, GL_FLOAT, sizeof(struct Vertex), vertex);
186     glColorPointer(3, GL_FLOAT, sizeof(struct Vertex), &vertex[0].r); // Pointer to the first color
