541     PIXELFORMATDESCRIPTOR pfd;
542     HGLRC share = NULL;
543 
544     if (ctxconfig->share)
545         share = ctxconfig->share->context.wgl.handle;
546 
547     window->context.wgl.dc = GetDC(window->win32.handle);
548     if (!window->context.wgl.dc)
549     {
550         _glfwInputError(GLFW_PLATFORM_ERROR,
551                         "WGL: Failed to retrieve DC for window");
552         return GLFW_FALSE;
