554 
555     pixelFormat = choosePixelFormatWGL(window, ctxconfig, fbconfig);
556     if (!pixelFormat)
557         return GLFW_FALSE;
558 
559     if (!DescribePixelFormat(window->context.wgl.dc,
560                              pixelFormat, sizeof(pfd), &pfd))
561     {
562         _glfwInputErrorWin32(GLFW_PLATFORM_ERROR,
563                              "WGL: Failed to retrieve PFD for selected pixel format");
564         return GLFW_FALSE;
565     }
566 
567     if (!SetPixelFormat(window->context.wgl.dc, pixelFormat, &pfd))
568     {
569         _glfwInputErrorWin32(GLFW_PLATFORM_ERROR,
570                              "WGL: Failed to set selected pixel format");
571         return GLFW_FALSE;
572     }
573 
574     if (ctxconfig->client == GLFW_OPENGL_API)
575     {
576         if (ctxconfig->forward)
577         {
578             if (!_glfw.wgl.ARB_create_context)
579             {
580                 _glfwInputError(GLFW_VERSION_UNAVAILABLE,
581                                 "WGL: A forward compatible OpenGL context requested but WGL_ARB_create_context is unavailable");
582                 return GLFW_FALSE;
583             }
584         }
585 
586         if (ctxconfig->profile)
587         {
588             if (!_glfw.wgl.ARB_create_context_profile)
589             {
590                 _glfwInputError(GLFW_VERSION_UNAVAILABLE,
591                                 "WGL: OpenGL profile requested but WGL_ARB_create_context_profile is unavailable");
592                 return GLFW_FALSE;
593             }
594         }
595     }
596     else
597     {
598         if (!_glfw.wgl.ARB_create_context ||
599             !_glfw.wgl.ARB_create_context_profile ||
600             !_glfw.wgl.EXT_create_context_es2_profile)
601         {
602             _glfwInputError(GLFW_API_UNAVAILABLE,
603                             "WGL: OpenGL ES requested but WGL_ARB_create_context_es2_profile is unavailable");
604             return GLFW_FALSE;
605         }
606     }
607 
608     if (_glfw.wgl.ARB_create_context)
609     {
610         int index = 0, mask = 0, flags = 0;
611 
612         if (ctxconfig->client == GLFW_OPENGL_API)
613         {
614             if (ctxconfig->forward)
615                 flags |= WGL_CONTEXT_FORWARD_COMPATIBLE_BIT_ARB;
616 
617             if (ctxconfig->profile == GLFW_OPENGL_CORE_PROFILE)
618                 mask |= WGL_CONTEXT_CORE_PROFILE_BIT_ARB;
619             else if (ctxconfig->profile == GLFW_OPENGL_COMPAT_PROFILE)
620                 mask |= WGL_CONTEXT_COMPATIBILITY_PROFILE_BIT_ARB;
621         }
622         else
623             mask |= WGL_CONTEXT_ES2_PROFILE_BIT_EXT;
624 
625         if (ctxconfig->debug)
626             flags |= WGL_CONTEXT_DEBUG_BIT_ARB;
627 
628         if (ctxconfig->robustness)
629         {
630             if (_glfw.wgl.ARB_create_context_robustness)
631             {
632                 if (ctxconfig->robustness == GLFW_NO_RESET_NOTIFICATION)
633                 {
634                     setAttrib(WGL_CONTEXT_RESET_NOTIFICATION_STRATEGY_ARB,
635                               WGL_NO_RESET_NOTIFICATION_ARB);
636                 }
637                 else if (ctxconfig->robustness == GLFW_LOSE_CONTEXT_ON_RESET)
638                 {
639                     setAttrib(WGL_CONTEXT_RESET_NOTIFICATION_STRATEGY_ARB,
640                               WGL_LOSE_CONTEXT_ON_RESET_ARB);
641                 }
642 
643                 flags |= WGL_CONTEXT_ROBUST_ACCESS_BIT_ARB;
644             }
645         }
646 
647         if (ctxconfig->release)
648         {
649             if (_glfw.wgl.ARB_context_flush_control)
650             {
651                 if (ctxconfig->release == GLFW_RELEASE_BEHAVIOR_NONE)
652                 {
653                     setAttrib(WGL_CONTEXT_RELEASE_BEHAVIOR_ARB,
654                               WGL_CONTEXT_RELEASE_BEHAVIOR_NONE_ARB);
655                 }
656                 else if (ctxconfig->release == GLFW_RELEASE_BEHAVIOR_FLUSH)
657                 {
658                     setAttrib(WGL_CONTEXT_RELEASE_BEHAVIOR_ARB,
659                               WGL_CONTEXT_RELEASE_BEHAVIOR_FLUSH_ARB);
660                 }
661             }
662         }
663 
664         if (ctxconfig->noerror)
665         {
666             if (_glfw.wgl.ARB_create_context_no_error)
667                 setAttrib(WGL_CONTEXT_OPENGL_NO_ERROR_ARB, GLFW_TRUE);
668         }
669 
670         // NOTE: Only request an explicitly versioned context when necessary, as
671         //       explicitly requesting version 1.0 does not always return the
672         //       highest version supported by the driver
673         if (ctxconfig->major != 1 || ctxconfig->minor != 0)
674         {
675             setAttrib(WGL_CONTEXT_MAJOR_VERSION_ARB, ctxconfig->major);
676             setAttrib(WGL_CONTEXT_MINOR_VERSION_ARB, ctxconfig->minor);
677         }
678 
679         if (flags)
680             setAttrib(WGL_CONTEXT_FLAGS_ARB, flags);
681 
682         if (mask)
683             setAttrib(WGL_CONTEXT_PROFILE_MASK_ARB, mask);
684 
685         setAttrib(0, 0);
686 
687         window->context.wgl.handle =
688             wglCreateContextAttribsARB(window->context.wgl.dc, share, attribs);
689         if (!window->context.wgl.handle)
690         {
691             const DWORD error = GetLastError();
692 
693             if (error == (0xc0070000 | ERROR_INVALID_VERSION_ARB))
694             {
695                 if (ctxconfig->client == GLFW_OPENGL_API)
696                 {
697                     _glfwInputError(GLFW_VERSION_UNAVAILABLE,
698                                     "WGL: Driver does not support OpenGL version %i.%i",
699                                     ctxconfig->major,
700                                     ctxconfig->minor);
701                 }
702                 else
703                 {
704                     _glfwInputError(GLFW_VERSION_UNAVAILABLE,
705                                     "WGL: Driver does not support OpenGL ES version %i.%i",
706                                     ctxconfig->major,
707                                     ctxconfig->minor);
708                 }
709             }
710             else if (error == (0xc0070000 | ERROR_INVALID_PROFILE_ARB))
711             {
712                 _glfwInputError(GLFW_VERSION_UNAVAILABLE,
713                                 "WGL: Driver does not support the requested OpenGL profile");
714             }
715             else if (error == (0xc0070000 | ERROR_INCOMPATIBLE_DEVICE_CONTEXTS_ARB))
716             {
717                 _glfwInputError(GLFW_INVALID_VALUE,
718                                 "WGL: The share context is not compatible with the requested context");
719             }
720             else
721             {
722                 if (ctxconfig->client == GLFW_OPENGL_API)
723                 {
724                     _glfwInputError(GLFW_VERSION_UNAVAILABLE,
725                                     "WGL: Failed to create OpenGL context");
726                 }
727                 else
728                 {
729                     _glfwInputError(GLFW_VERSION_UNAVAILABLE,
730                                     "WGL: Failed to create OpenGL ES context");
731                 }
732             }
733 
734             return GLFW_FALSE;
735         }
736     }
737     else
738     {
739         window->context.wgl.handle = wglCreateContext(window->context.wgl.dc);
740         if (!window->context.wgl.handle)
741         {
742             _glfwInputErrorWin32(GLFW_VERSION_UNAVAILABLE,
743                                  "WGL: Failed to create OpenGL context");
744             return GLFW_FALSE;
745         }
746 
747         if (share)
748         {
749             if (!wglShareLists(share, window->context.wgl.handle))
750             {
751                 _glfwInputErrorWin32(GLFW_PLATFORM_ERROR,
752                                      "WGL: Failed to enable sharing with specified OpenGL context");
753                 return GLFW_FALSE;
754             }
755         }
756     }
757 
758     window->context.makeCurrent = makeContextCurrentWGL;
759     window->context.swapBuffers = swapBuffersWGL;
760     window->context.swapInterval = swapIntervalWGL;
761     window->context.extensionSupported = extensionSupportedWGL;
762     window->context.getProcAddress = getProcAddressWGL;
763     window->context.destroy = destroyContextWGL;
764 
765     return GLFW_TRUE;
