439     ZeroMemory(&pfd, sizeof(pfd));
440     pfd.nSize = sizeof(pfd);
441     pfd.nVersion = 1;
442     pfd.dwFlags = PFD_DRAW_TO_WINDOW | PFD_SUPPORT_OPENGL | PFD_DOUBLEBUFFER;
443     pfd.iPixelType = PFD_TYPE_RGBA;
444     pfd.cColorBits = 24;
445 
446     if (!SetPixelFormat(dc, ChoosePixelFormat(dc, &pfd), &pfd))
447     {
