460 
461     pdc = wglGetCurrentDC();
462     prc = wglGetCurrentContext();
463 
464     if (!wglMakeCurrent(dc, rc))
465     {
466         _glfwInputErrorWin32(GLFW_PLATFORM_ERROR,
467                              "WGL: Failed to make dummy context current");
468         wglMakeCurrent(pdc, prc);
469         wglDeleteContext(rc);
470         return GLFW_FALSE;
471     }
