771 {
772     _GLFWwindow* window = (_GLFWwindow*) handle;
773     _GLFW_REQUIRE_INIT_OR_RETURN(NULL);
774 
775     if (_glfw.platform.platformID != GLFW_PLATFORM_WIN32)
776     {
777         _glfwInputError(GLFW_PLATFORM_UNAVAILABLE,
778                         "WGL: Platform not initialized");
779         return NULL;
780     }
781 
782     if (window->context.source != GLFW_NATIVE_CONTEXT_API)
783     {
784         _glfwInputError(GLFW_NO_WINDOW_CONTEXT, NULL);
785         return NULL;
786     }
787 
