{
    if (maximized)
    {
        // The window was maximized
    }
    else
    {
        // The window was restored
    }
}

void window_focus_callback(GLFWwindow* window, int focused)
{
    if (focused)
    {
        // The window gained input focus
    }
    else
    {
        // The window lost input focus
    }
}

