1500     // If we're not in the correct window just save the cursor
1501     // the next time the pointer enters the window the cursor will change
1502     if (window != _glfw.wl.pointerFocus || window->wl.decorations.focus != mainWindow)
1503         return;
1504 
1505     // Unlock possible pointer lock if no longer disabled.
1506     if (window->cursorMode != GLFW_CURSOR_DISABLED && isPointerLocked(window))
1507         unlockPointer(window);
1508 
1509     if (window->cursorMode == GLFW_CURSOR_NORMAL)
1510     {
1511         if (cursor)
1512             setCursorImage(window, &cursor->wl);
1513         else
1514         {
1515             defaultCursor = wl_cursor_theme_get_cursor(_glfw.wl.cursorTheme,
1516                                                        "left_ptr");
1517             if (!defaultCursor)
1518             {
1519                 _glfwInputError(GLFW_PLATFORM_ERROR,
1520                                 "Wayland: Standard cursor not found");
1521                 return;
1522             }
1523             if (_glfw.wl.cursorThemeHiDPI)
1524                 defaultCursorHiDPI =
1525                     wl_cursor_theme_get_cursor(_glfw.wl.cursorThemeHiDPI,
1526                                                "left_ptr");
1527             _GLFWcursorWayland cursorWayland = {
1528                 defaultCursor,
1529                 defaultCursorHiDPI,
1530                 NULL,
1531                 0, 0,
1532                 0, 0,
1533                 0
1534             };
1535             setCursorImage(window, &cursorWayland);
1536         }
1537     }
1538     else if (window->cursorMode == GLFW_CURSOR_DISABLED)
1539     {
1540         if (!isPointerLocked(window))
1541             lockPointer(window);
1542     }
1543     else if (window->cursorMode == GLFW_CURSOR_HIDDEN)
1544     {
