1688 {
1689     int fds[2];
1690     int ret;
1691     size_t len = 0;
1692 
1693     if (!_glfw.wl.dataOffer)
1694     {
1695         _glfwInputError(GLFW_FORMAT_UNAVAILABLE,
1696                         "No clipboard data has been sent yet");
1697         return NULL;
1698     }
1699 
1700     ret = pipe2(fds, O_CLOEXEC);
1701     if (ret < 0)
1702     {
1703         // TODO: also report errno maybe?
1704         _glfwInputError(GLFW_PLATFORM_ERROR,
1705                         "Wayland: Impossible to create clipboard pipe fds");
1706         return NULL;
1707     }
1708 
1709     wl_data_offer_receive(_glfw.wl.dataOffer, "text/plain;charset=utf-8", fds[1]);
1710     close(fds[1]);
1711 
1712     // XXX: this is a huge hack, this function shouldnt be synchronous!
1713     handleEvents(-1);
1714 
1715     while (1)
1716     {
1717         // Grow the clipboard if we need to paste something bigger, there is no
1718         // shrink operation yet.
1719         if (len + 4096 > _glfw.wl.clipboardSize)
1720         {
1721             if (!growClipboardString())
1722             {
1723                 close(fds[0]);
1724                 return NULL;
1725             }
1726         }
1727 
1728         // Then read from the fd to the clipboard, handling all known errors.
1729         ret = read(fds[0], _glfw.wl.clipboardString + len, 4096);
1730         if (ret == 0)
1731             break;
1732         if (ret == -1 && errno == EINTR)
1733             continue;
1734         if (ret == -1)
1735         {
1736             // TODO: also report errno maybe.
1737             _glfwInputError(GLFW_PLATFORM_ERROR,
1738                             "Wayland: Impossible to read from clipboard fd");
1739             close(fds[0]);
1740             return NULL;
1741         }
1742         len += ret;
1743     }
1744     close(fds[0]);
1745     if (len + 1 > _glfw.wl.clipboardSize)
1746     {
1747         if (!growClipboardString())
1748             return NULL;
1749     }
1750     _glfw.wl.clipboardString[len] = '\0';
1751     return _glfw.wl.clipboardString;
1752 }

