1206 
1207     const int key = _glfw.wl.keycodes[scancode];
1208     const xkb_keycode_t keycode = scancode + 8;
1209     const xkb_layout_index_t layout =
1210         xkb_state_key_get_layout(_glfw.wl.xkb.state, keycode);
1211     if (layout == XKB_LAYOUT_INVALID)
1212     {
1213         _glfwInputError(GLFW_PLATFORM_ERROR,
1214                         "Wayland: Failed to retrieve layout for key name");
1215         return NULL;
1216     }
1217 
1218     const xkb_keysym_t* keysyms = NULL;
1219     xkb_keymap_key_get_syms_by_level(_glfw.wl.xkb.keymap,
1220                                      keycode,
1221                                      layout,
1222                                      0,
1223                                      &keysyms);
1224     if (keysyms == NULL)
1225     {
1226         _glfwInputError(GLFW_PLATFORM_ERROR,
1227                         "Wayland: Failed to retrieve keysym for key name");
1228         return NULL;
1229     }
1230 
1231     const uint32_t codepoint = _glfwKeySym2Unicode(keysyms[0]);
1232     if (codepoint == GLFW_INVALID_CODEPOINT)
1233     {
1234         _glfwInputError(GLFW_PLATFORM_ERROR,
1235                         "Wayland: Failed to retrieve codepoint for key name");
1236         return NULL;
1237     }
1238 
1239     const size_t count = _glfwEncodeUTF8(_glfw.wl.keynames[key],  codepoint);
1240     if (count == 0)
1241     {
1242         _glfwInputError(GLFW_PLATFORM_ERROR,
1243                         "Wayland: Failed to encode codepoint for key name");
1244         return NULL;
1245     }
1246 
1247     _glfw.wl.keynames[key][count] = '\0';
1248     return _glfw.wl.keynames[key];
