923         xdg_toplevel_set_max_size(window->wl.xdg.toplevel, maxwidth, maxheight);
924         wl_surface_commit(window->wl.surface);
925     }
926 }

906 {
907     window->wl.width = width;
908     window->wl.height = height;
909     resizeWindow(window);
910 }

865 {
866     if (window->wl.title)
867         _glfw_free(window->wl.title);
868     window->wl.title = _glfw_strdup(title);
869     if (window->wl.xdg.toplevel)
870         xdg_toplevel_set_title(window->wl.xdg.toplevel, title);
871 }

1002 {
1003     if (!window->wl.visible)
1004     {
1005         // NOTE: The XDG surface and role are created here so command-line applications
1006         //       with off-screen windows do not appear in for example the Unity dock
1007         if (!window->wl.xdg.toplevel)
1008             createXdgSurface(window);
1009 
1010         window->wl.visible = GLFW_TRUE;
1011         _glfwInputWindowDamage(window);
1012     }
1013 }

1161 {
1162     handleEvents((int) (timeout * 1e3));
1163 }

1060 {
1061     return _glfw.wl.keyboardFocus == window;
1062 }

1082 {
1083     return window->wl.hovered;
1084 }

1065 {
1066     // xdg-shell doesnt give any way to request whether a surface is
1067     // iconified.
1068     return GLFW_FALSE;
1069 }

1077 {
1078     return window->wl.maximized;
1079 }

1072 {
1073     return window->wl.visible;
1074 }

1841 {
1842     _GLFW_REQUIRE_INIT_OR_RETURN(NULL);
1843 
1844     if (_glfw.platform.platformID != GLFW_PLATFORM_WAYLAND)
1845     {
1846         _glfwInputError(GLFW_PLATFORM_UNAVAILABLE,
1847                         "Wayland: Platform not initialized");
1848         return NULL;
1849     }
1850 
1851     return _glfw.wl.display;
1852 }

1855 {
1856     _GLFWwindow* window = (_GLFWwindow*) handle;
1857     _GLFW_REQUIRE_INIT_OR_RETURN(NULL);
1858 
1859     if (_glfw.platform.platformID != GLFW_PLATFORM_WAYLAND)
1860     {
1861         _glfwInputError(GLFW_PLATFORM_UNAVAILABLE,
1862                         "Wayland: Platform not initialized");
1863         return NULL;
1864     }
1865 
1866     return window->wl.surface;
1867 }
