516 
517         if (ctxconfig->debug)
518             flags |= GLX_CONTEXT_DEBUG_BIT_ARB;
519 
520         if (ctxconfig->robustness)
521         {
522             if (_glfw.glx.ARB_create_context_robustness)
523             {
524                 if (ctxconfig->robustness == GLFW_NO_RESET_NOTIFICATION)
525                 {
526                     setAttrib(GLX_CONTEXT_RESET_NOTIFICATION_STRATEGY_ARB,
527                               GLX_NO_RESET_NOTIFICATION_ARB);
528                 }
529                 else if (ctxconfig->robustness == GLFW_LOSE_CONTEXT_ON_RESET)
530                 {
531                     setAttrib(GLX_CONTEXT_RESET_NOTIFICATION_STRATEGY_ARB,
532                               GLX_LOSE_CONTEXT_ON_RESET_ARB);
533                 }
534 
535                 flags |= GLX_CONTEXT_ROBUST_ACCESS_BIT_ARB;
536             }
537         }
538 
539         if (ctxconfig->release)
540         {
541             if (_glfw.glx.ARB_context_flush_control)
542             {
543                 if (ctxconfig->release == GLFW_RELEASE_BEHAVIOR_NONE)
544                 {
545                     setAttrib(GLX_CONTEXT_RELEASE_BEHAVIOR_ARB,
546                               GLX_CONTEXT_RELEASE_BEHAVIOR_NONE_ARB);
547                 }
548                 else if (ctxconfig->release == GLFW_RELEASE_BEHAVIOR_FLUSH)
549                 {
550                     setAttrib(GLX_CONTEXT_RELEASE_BEHAVIOR_ARB,
551                               GLX_CONTEXT_RELEASE_BEHAVIOR_FLUSH_ARB);
552                 }
553             }
554         }
555 
556         if (ctxconfig->noerror)
557         {
558             if (_glfw.glx.ARB_create_context_no_error)
559                 setAttrib(GLX_CONTEXT_OPENGL_NO_ERROR_ARB, GLFW_TRUE);
560         }
561 
562         // NOTE: Only request an explicitly versioned context when necessary, as
563         //       explicitly requesting version 1.0 does not always return the
564         //       highest version supported by the driver
565         if (ctxconfig->major != 1 || ctxconfig->minor != 0)
566         {
567             setAttrib(GLX_CONTEXT_MAJOR_VERSION_ARB, ctxconfig->major);
568             setAttrib(GLX_CONTEXT_MINOR_VERSION_ARB, ctxconfig->minor);
569         }
570 
571         if (mask)
572             setAttrib(GLX_CONTEXT_PROFILE_MASK_ARB, mask);
573 
574         if (flags)
575             setAttrib(GLX_CONTEXT_FLAGS_ARB, flags);
576 
577         setAttrib(None, None);
578 
579         window->context.glx.handle =
580             _glfw.glx.CreateContextAttribsARB(_glfw.x11.display,
581                                               native,
582                                               share,
583                                               True,
584                                               attribs);
585 
586         // HACK: This is a fallback for broken versions of the Mesa
587         //       implementation of GLX_ARB_create_context_profile that fail
588         //       default 1.0 context creation with a GLXBadProfileARB error in
589         //       violation of the extension spec
590         if (!window->context.glx.handle)
591         {
592             if (_glfw.x11.errorCode == _glfw.glx.errorBase + GLXBadProfileARB &&
593                 ctxconfig->client == GLFW_OPENGL_API &&
