599             }
600         }
601     }
602     else
603     {
604         window->context.glx.handle =
605             createLegacyContextGLX(window, native, share);
606     }
607 
608     _glfwReleaseErrorHandlerX11();
609 
610     if (!window->context.glx.handle)
611     {
612         _glfwInputErrorX11(GLFW_VERSION_UNAVAILABLE, "GLX: Failed to create context");
613         return GLFW_FALSE;
614     }
615 
616     window->context.glx.window =
617         glXCreateWindow(_glfw.x11.display, native, window->x11.handle, NULL);
618     if (!window->context.glx.window)
619     {
620         _glfwInputError(GLFW_PLATFORM_ERROR, "GLX: Failed to create window");
621         return GLFW_FALSE;
622     }
623 
624     window->context.makeCurrent = makeContextCurrentGLX;
