1092 {
1093     Cursor cursor;
1094 
1095     if (!_glfw.x11.xcursor.handle)
1096         return None;
1097 
1098     XcursorImage* native = XcursorImageCreate(image->width, image->height);
1099     if (native == NULL)
1100         return None;
1101 
1102     native->xhot = xhot;
1103     native->yhot = yhot;
1104 
1105     unsigned char* source = (unsigned char*) image->pixels;
1106     XcursorPixel* target = native->pixels;
1107 
1108     for (int i = 0;  i < image->width * image->height;  i++, target++, source += 4)
1109     {
1110         unsigned int alpha = source[3];
1111 
1112         *target = (alpha << 24) |
1113                   ((unsigned char) ((source[0] * alpha) / 255) << 16) |
1114                   ((unsigned char) ((source[1] * alpha) / 255) <<  8) |
1115                   ((unsigned char) ((source[2] * alpha) / 255) <<  0);
1116     }
1117 
1118     cursor = XcursorImageLoadCursor(_glfw.x11.display, native);
1119     XcursorImageDestroy(native);
1120 
1121     return cursor;
1122 }

