1944 {
1945     Visual* visual = NULL;
1946     int depth;
1947 
1948     if (ctxconfig->client != GLFW_NO_API)
1949     {
1950         if (ctxconfig->source == GLFW_NATIVE_CONTEXT_API)
1951         {
1952             if (!_glfwInitGLX())
1953                 return GLFW_FALSE;
1954             if (!_glfwChooseVisualGLX(wndconfig, ctxconfig, fbconfig, &visual, &depth))
1955                 return GLFW_FALSE;
1956         }
1957         else if (ctxconfig->source == GLFW_EGL_CONTEXT_API)
1958         {
1959             if (!_glfwInitEGL())
1960                 return GLFW_FALSE;
1961             if (!_glfwChooseVisualEGL(wndconfig, ctxconfig, fbconfig, &visual, &depth))
1962                 return GLFW_FALSE;
1963         }
1964         else if (ctxconfig->source == GLFW_OSMESA_CONTEXT_API)
1965         {
1966             if (!_glfwInitOSMesa())
1967                 return GLFW_FALSE;
1968         }
1969     }
1970 
1971     if (!visual)
1972     {
1973         visual = DefaultVisual(_glfw.x11.display, _glfw.x11.screen);
1974         depth = DefaultDepth(_glfw.x11.display, _glfw.x11.screen);
1975     }
1976 
1977     if (!createNativeWindow(window, wndconfig, visual, depth))
1978         return GLFW_FALSE;
1979 
1980     if (ctxconfig->client != GLFW_NO_API)
1981     {
1982         if (ctxconfig->source == GLFW_NATIVE_CONTEXT_API)
1983         {
1984             if (!_glfwCreateContextGLX(window, ctxconfig, fbconfig))
1985                 return GLFW_FALSE;
1986         }
1987         else if (ctxconfig->source == GLFW_EGL_CONTEXT_API)
1988         {
1989             if (!_glfwCreateContextEGL(window, ctxconfig, fbconfig))
1990                 return GLFW_FALSE;
1991         }
1992         else if (ctxconfig->source == GLFW_OSMESA_CONTEXT_API)
1993         {
1994             if (!_glfwCreateContextOSMesa(window, ctxconfig, fbconfig))
1995                 return GLFW_FALSE;
1996         }
1997     }
1998 
1999     if (window->monitor)
2000     {
2001         _glfwShowWindowX11(window);
2002         updateWindowMode(window);
2003         acquireMonitor(window);
2004     }
2005 
2006     XFlush(_glfw.x11.display);
2007     return GLFW_TRUE;
2008 }

