2211 {
2212     long* extents = NULL;
2213 
2214     if (window->monitor || !window->decorated)
2215         return;
2216 
2217     if (_glfw.x11.NET_FRAME_EXTENTS == None)
2218         return;
2219 
2220     if (!_glfwWindowVisibleX11(window) &&
2221         _glfw.x11.NET_REQUEST_FRAME_EXTENTS)
2222     {
2223         XEvent event;
2224         double timeout = 0.5;
2225 
2226         // Ensure _NET_FRAME_EXTENTS is set, allowing glfwGetWindowFrameSize to
2227         // function before the window is mapped
2228         sendEventToWM(window, _glfw.x11.NET_REQUEST_FRAME_EXTENTS,
2229                       0, 0, 0, 0, 0);
2230 
2231         // HACK: Use a timeout because earlier versions of some window managers
2232         //       (at least Unity, Fluxbox and Xfwm) failed to send the reply
2233         //       They have been fixed but broken versions are still in the wild
2234         //       If you are affected by this and your window manager is NOT
2235         //       listed above, PLEASE report it to their and our issue trackers
2236         while (!XCheckIfEvent(_glfw.x11.display,
2237                               &event,
2238                               isFrameExtentsEvent,
2239                               (XPointer) window))
2240         {
2241             if (!waitForEvent(&timeout))
2242             {
2243                 _glfwInputError(GLFW_PLATFORM_ERROR,
2244                                 "X11: The window manager has a broken _NET_REQUEST_FRAME_EXTENTS implementation; please report this issue");
2245                 return;
2246             }
2247         }
2248     }
2249 
2250     if (_glfwGetWindowPropertyX11(window->x11.handle,
2251                                   _glfw.x11.NET_FRAME_EXTENTS,
2252                                   XA_CARDINAL,
2253                                   (unsigned char**) &extents) == 4)
2254     {
2255         if (left)
2256             *left = extents[0];
2257         if (top)
2258             *top = extents[2];
2259         if (right)
2260             *right = extents[1];
2261         if (bottom)
2262             *bottom = extents[3];
2263     }
2264 
2265     if (extents)
2266         XFree(extents);
2267 }

