1081 {
1082     char buffer[_GLFW_MESSAGE_SIZE];
1083     XGetErrorText(_glfw.x11.display, _glfw.x11.errorCode,
1084                   buffer, sizeof(buffer));
1085 
1086     _glfwInputError(error, "%s: %s", message, buffer);
