1847 {
1848     if (!_glfw.x11.xrender.available)
1849         return GLFW_FALSE;
1850 
1851     XRenderPictFormat* pf = XRenderFindVisualFormat(_glfw.x11.display, visual);
1852     return pf && pf->direct.alphaMask;
1853 }

2327 {
2328     if (!_glfw.x11.NET_WM_STATE ||
2329         !_glfw.x11.NET_WM_STATE_MAXIMIZED_VERT ||
2330         !_glfw.x11.NET_WM_STATE_MAXIMIZED_HORZ)
2331     {
2332         return;
2333     }
2334 
2335     if (_glfwWindowVisibleX11(window))
2336     {
2337         sendEventToWM(window,
2338                     _glfw.x11.NET_WM_STATE,
2339                     _NET_WM_STATE_ADD,
2340                     _glfw.x11.NET_WM_STATE_MAXIMIZED_VERT,
2341                     _glfw.x11.NET_WM_STATE_MAXIMIZED_HORZ,
2342                     1, 0);
2343     }
2344     else
2345     {
2346         Atom* states = NULL;
2347         unsigned long count =
2348             _glfwGetWindowPropertyX11(window->x11.handle,
2349                                       _glfw.x11.NET_WM_STATE,
2350                                       XA_ATOM,
2351                                       (unsigned char**) &states);
2352 
2353         // NOTE: We don't check for failure as this property may not exist yet
2354         //       and that's fine (and we'll create it implicitly with append)
2355 
2356         Atom missing[2] =
2357         {
2358             _glfw.x11.NET_WM_STATE_MAXIMIZED_VERT,
2359             _glfw.x11.NET_WM_STATE_MAXIMIZED_HORZ
2360         };
2361         unsigned long missingCount = 2;
2362 
2363         for (unsigned long i = 0;  i < count;  i++)
2364         {
2365             for (unsigned long j = 0;  j < missingCount;  j++)
2366             {
2367                 if (states[i] == missing[j])
2368                 {
2369                     missing[j] = missing[missingCount - 1];
2370                     missingCount--;
2371                 }
2372             }
2373         }
2374 
2375         if (states)
2376             XFree(states);
2377 
2378         if (!missingCount)
2379             return;
2380 
2381         XChangeProperty(_glfw.x11.display, window->x11.handle,
2382                         _glfw.x11.NET_WM_STATE, XA_ATOM, 32,
2383                         PropModeAppend,
2384                         (unsigned char*) missing,
2385                         missingCount);
2386     }
2387 
2388     XFlush(_glfw.x11.display);
2389 }

2746 {
2747     _GLFWwindow* window;
2748 
2749 #if defined(__linux__)
2750     if (_glfw.joysticksInitialized)
2751         _glfwDetectJoystickConnectionLinux();
2752 #endif
2753     XPending(_glfw.x11.display);
2754 
2755     while (QLength(_glfw.x11.display))
2756     {
2757         XEvent event;
2758         XNextEvent(_glfw.x11.display, &event);
2759         processEvent(&event);
2760     }
2761 
2762     window = _glfw.x11.disabledCursorWindow;
2763     if (window)
2764     {
2765         int width, height;
2766         _glfwGetWindowSizeX11(window, &width, &height);
2767 
2768         // NOTE: Re-center the cursor only if it has moved since the last call,
2769         //       to avoid breaking glfwWaitEvents with MotionNotify
2770         if (window->x11.lastCursorPosX != width / 2 ||
2771             window->x11.lastCursorPosY != height / 2)
2772         {
2773             _glfwSetCursorPosX11(window, width / 2, height / 2);
2774         }
2775     }
2776 
2777     XFlush(_glfw.x11.display);
2778 }

102 {
103     if (_glfw.x11.randr.available && !_glfw.x11.randr.monitorBroken)
104     {
105         int disconnectedCount, screenCount = 0;
106         _GLFWmonitor** disconnected = NULL;
107         XineramaScreenInfo* screens = NULL;
108         XRRScreenResources* sr = XRRGetScreenResourcesCurrent(_glfw.x11.display,
109                                                               _glfw.x11.root);
110         RROutput primary = XRRGetOutputPrimary(_glfw.x11.display,
111                                                _glfw.x11.root);
112 
113         if (_glfw.x11.xinerama.available)
114             screens = XineramaQueryScreens(_glfw.x11.display, &screenCount);
115 
116         disconnectedCount = _glfw.monitorCount;
117         if (disconnectedCount)
118         {
119             disconnected = _glfw_calloc(_glfw.monitorCount, sizeof(_GLFWmonitor*));
120             memcpy(disconnected,
121                    _glfw.monitors,
122                    _glfw.monitorCount * sizeof(_GLFWmonitor*));
123         }
124 
125         for (int i = 0;  i < sr->noutput;  i++)
126         {
127             int j, type, widthMM, heightMM;
128 
129             XRROutputInfo* oi = XRRGetOutputInfo(_glfw.x11.display, sr, sr->outputs[i]);
130             if (oi->connection != RR_Connected || oi->crtc == None)
131             {
132                 XRRFreeOutputInfo(oi);
133                 continue;
134             }
135 
136             for (j = 0;  j < disconnectedCount;  j++)
137             {
138                 if (disconnected[j] &&
139                     disconnected[j]->x11.output == sr->outputs[i])
140                 {
141                     disconnected[j] = NULL;
142                     break;
143                 }
144             }
145 
146             if (j < disconnectedCount)
147             {
148                 XRRFreeOutputInfo(oi);
149                 continue;
150             }
151 
152             XRRCrtcInfo* ci = XRRGetCrtcInfo(_glfw.x11.display, sr, oi->crtc);
153             if (ci->rotation == RR_Rotate_90 || ci->rotation == RR_Rotate_270)
154             {
155                 widthMM  = oi->mm_height;
156                 heightMM = oi->mm_width;
157             }
158             else
159             {
160                 widthMM  = oi->mm_width;
161                 heightMM = oi->mm_height;
162             }
163 
164             if (widthMM <= 0 || heightMM <= 0)
165             {
166                 // HACK: If RandR does not provide a physical size, assume the
167                 //       X11 default 96 DPI and calculate from the CRTC viewport
168                 // NOTE: These members are affected by rotation, unlike the mode
169                 //       info and output info members
170                 widthMM  = (int) (ci->width * 25.4f / 96.f);
171                 heightMM = (int) (ci->height * 25.4f / 96.f);
172             }
173 
174             _GLFWmonitor* monitor = _glfwAllocMonitor(oi->name, widthMM, heightMM);
175             monitor->x11.output = sr->outputs[i];
176             monitor->x11.crtc   = oi->crtc;
177 
178             for (j = 0;  j < screenCount;  j++)
179             {
180                 if (screens[j].x_org == ci->x &&
181                     screens[j].y_org == ci->y &&
182                     screens[j].width == ci->width &&
183                     screens[j].height == ci->height)
184                 {
185                     monitor->x11.index = j;
186                     break;
187                 }
188             }
189 
190             if (monitor->x11.output == primary)
191                 type = _GLFW_INSERT_FIRST;
192             else
193                 type = _GLFW_INSERT_LAST;
194 
195             _glfwInputMonitor(monitor, GLFW_CONNECTED, type);
196 
197             XRRFreeOutputInfo(oi);
198             XRRFreeCrtcInfo(ci);
199         }
200 
201         XRRFreeScreenResources(sr);
202 
203         if (screens)
204             XFree(screens);
205 
206         for (int i = 0;  i < disconnectedCount;  i++)
207         {
208             if (disconnected[i])
209                 _glfwInputMonitor(disconnected[i], GLFW_DISCONNECTED, 0);
210         }
211 
212         _glfw_free(disconnected);
213     }
214     else
215     {
216         const int widthMM = DisplayWidthMM(_glfw.x11.display, _glfw.x11.screen);
217         const int heightMM = DisplayHeightMM(_glfw.x11.display, _glfw.x11.screen);
218 
219         _glfwInputMonitor(_glfwAllocMonitor("Display", widthMM, heightMM),
220                           GLFW_CONNECTED,
221                           _GLFW_INSERT_FIRST);
222     }
223 }

2800 {
2801     XEvent event = { ClientMessage };
2802     event.xclient.window = _glfw.x11.helperWindowHandle;
2803     event.xclient.format = 32; // Data is 32-bit longs
2804     event.xclient.message_type = _glfw.x11.NULL_;
2805 
2806     XSendEvent(_glfw.x11.display, _glfw.x11.helperWindowHandle, False, 0, &event);
2807     XFlush(_glfw.x11.display);
2808 }

1858 {
1859     XConvertSelection(_glfw.x11.display,
1860                       _glfw.x11.CLIPBOARD_MANAGER,
1861                       _glfw.x11.SAVE_TARGETS,
1862                       None,
1863                       _glfw.x11.helperWindowHandle,
1864                       CurrentTime);
1865 
1866     for (;;)
1867     {
1868         XEvent event;
1869 
1870         while (XCheckIfEvent(_glfw.x11.display, &event, isSelectionEvent, NULL))
1871         {
1872             switch (event.type)
1873             {
1874                 case SelectionRequest:
1875                     handleSelectionRequest(&event);
1876                     break;
1877 
1878                 case SelectionClear:
1879                     handleSelectionClear(&event);
1880                     break;
1881 
1882                 case SelectionNotify:
1883                 {
1884                     if (event.xselection.target == _glfw.x11.SAVE_TARGETS)
1885                     {
1886                         // This means one of two things; either the selection
1887                         // was not owned, which means there is no clipboard
1888                         // manager, or the transfer to the clipboard manager has
1889                         // completed
1890                         // In either case, it means we are done here
1891                         return;
1892                     }
1893 
1894                     break;
1895                 }
1896             }
1897         }
1898 
1899         waitForEvent(NULL);
1900     }
1901 }

