2588 {
2589     struct
2590     {
2591         unsigned long flags;
2592         unsigned long functions;
2593         unsigned long decorations;
2594         long input_mode;
2595         unsigned long status;
2596     } hints = {0};
2597 
2598     hints.flags = MWM_HINTS_DECORATIONS;
2599     hints.decorations = enabled ? MWM_DECOR_ALL : 0;
2600 
2601     XChangeProperty(_glfw.x11.display, window->x11.handle,
2602                     _glfw.x11.MOTIF_WM_HINTS,
2603                     _glfw.x11.MOTIF_WM_HINTS, 32,
2604                     PropModeReplace,
2605                     (unsigned char*) &hints,
2606                     sizeof(hints) / sizeof(long));
2607 }

2610 {
2611     if (!_glfw.x11.NET_WM_STATE || !_glfw.x11.NET_WM_STATE_ABOVE)
2612         return;
2613 
2614     if (_glfwWindowVisibleX11(window))
2615     {
2616         const long action = enabled ? _NET_WM_STATE_ADD : _NET_WM_STATE_REMOVE;
2617         sendEventToWM(window,
2618                       _glfw.x11.NET_WM_STATE,
2619                       action,
2620                       _glfw.x11.NET_WM_STATE_ABOVE,
2621                       0, 1, 0);
2622     }
2623     else
2624     {
2625         Atom* states = NULL;
2626         const unsigned long count =
2627             _glfwGetWindowPropertyX11(window->x11.handle,
2628                                       _glfw.x11.NET_WM_STATE,
2629                                       XA_ATOM,
2630                                       (unsigned char**) &states);
2631 
2632         // NOTE: We don't check for failure as this property may not exist yet
2633         //       and that's fine (and we'll create it implicitly with append)
2634 
2635         if (enabled)
2636         {
2637             unsigned long i;
2638 
2639             for (i = 0;  i < count;  i++)
2640             {
2641                 if (states[i] == _glfw.x11.NET_WM_STATE_ABOVE)
2642                     break;
2643             }
2644 
2645             if (i == count)
2646             {
2647                 XChangeProperty(_glfw.x11.display, window->x11.handle,
2648                                 _glfw.x11.NET_WM_STATE, XA_ATOM, 32,
2649                                 PropModeAppend,
2650                                 (unsigned char*) &_glfw.x11.NET_WM_STATE_ABOVE,
2651                                 1);
2652             }
2653         }
2654         else if (states)
2655         {
2656             for (unsigned long i = 0;  i < count;  i++)
2657             {
2658                 if (states[i] == _glfw.x11.NET_WM_STATE_ABOVE)
2659                 {
2660                     states[i] = states[count - 1];
2661                     XChangeProperty(_glfw.x11.display, window->x11.handle,
2662                                     _glfw.x11.NET_WM_STATE, XA_ATOM, 32,
2663                                     PropModeReplace, (unsigned char*) states, count - 1);
2664                     break;
2665                 }
2666             }
2667         }
2668 
2669         if (states)
2670             XFree(states);
2671     }
2672 
2673     XFlush(_glfw.x11.display);
2674 }

2069 {
2070     if (count)
2071     {
2072         int longCount = 0;
2073 
2074         for (int i = 0;  i < count;  i++)
2075             longCount += 2 + images[i].width * images[i].height;
2076 
2077         unsigned long* icon = _glfw_calloc(longCount, sizeof(unsigned long));
2078         unsigned long* target = icon;
2079 
2080         for (int i = 0;  i < count;  i++)
2081         {
2082             *target++ = images[i].width;
2083             *target++ = images[i].height;
2084 
2085             for (int j = 0;  j < images[i].width * images[i].height;  j++)
2086             {
2087                 *target++ = (((unsigned long) images[i].pixels[j * 4 + 0]) << 16) |
2088                             (((unsigned long) images[i].pixels[j * 4 + 1]) <<  8) |
2089                             (((unsigned long) images[i].pixels[j * 4 + 2]) <<  0) |
2090                             (((unsigned long) images[i].pixels[j * 4 + 3]) << 24);
2091             }
2092         }
2093 
2094         // NOTE: XChangeProperty expects 32-bit values like the image data above to be
2095         //       placed in the 32 least significant bits of individual longs.  This is
2096         //       true even if long is 64-bit and a WM protocol calls for "packed" data.
2097         //       This is because of a historical mistake that then became part of the Xlib
2098         //       ABI.  Xlib will pack these values into a regular array of 32-bit values
2099         //       before sending it over the wire.
2100         XChangeProperty(_glfw.x11.display, window->x11.handle,
2101                         _glfw.x11.NET_WM_ICON,
2102                         XA_CARDINAL, 32,
2103                         PropModeReplace,
2104                         (unsigned char*) icon,
2105                         longCount);
2106 
2107         _glfw_free(icon);
2108     }
2109     else
2110     {
2111         XDeleteProperty(_glfw.x11.display, window->x11.handle,
2112                         _glfw.x11.NET_WM_ICON);
2113     }
2114 
2115     XFlush(_glfw.x11.display);
2116 }

