1554 
1555     if (_glfw.x11.x11xcb.handle)
1556     {
1557         _glfwPlatformFreeModule(_glfw.x11.x11xcb.handle);
1558         _glfw.x11.x11xcb.handle = NULL;
1559     }
1560 
1561     if (_glfw.x11.xcursor.handle)
1562     {
1563         _glfwPlatformFreeModule(_glfw.x11.xcursor.handle);
1564         _glfw.x11.xcursor.handle = NULL;
1565     }
1566 
1567     if (_glfw.x11.randr.handle)
1568     {
1569         _glfwPlatformFreeModule(_glfw.x11.randr.handle);
1570         _glfw.x11.randr.handle = NULL;
1571     }
1572 
1573     if (_glfw.x11.xinerama.handle)
1574     {
1575         _glfwPlatformFreeModule(_glfw.x11.xinerama.handle);
1576         _glfw.x11.xinerama.handle = NULL;
1577     }
1578 
1579     if (_glfw.x11.xrender.handle)
1580     {
1581         _glfwPlatformFreeModule(_glfw.x11.xrender.handle);
1582         _glfw.x11.xrender.handle = NULL;
1583     }
1584 
1585     if (_glfw.x11.vidmode.handle)
1586     {
1587         _glfwPlatformFreeModule(_glfw.x11.vidmode.handle);
1588         _glfw.x11.vidmode.handle = NULL;
1589     }
1590 
1591     if (_glfw.x11.xi.handle)
1592     {
1593         _glfwPlatformFreeModule(_glfw.x11.xi.handle);
1594         _glfw.x11.xi.handle = NULL;
1595     }
1596 
1597     // NOTE: These need to be unloaded after XCloseDisplay, as they register
1598     //       cleanup callbacks that get called by that function
1599     _glfwTerminateEGL();
1600     _glfwTerminateGLX();
1601 
1602     if (_glfw.x11.xlib.handle)
1603     {
1604         _glfwPlatformFreeModule(_glfw.x11.xlib.handle);
1605         _glfw.x11.xlib.handle = NULL;
1606     }
1607 }

2789 {
2790     while (!XPending(_glfw.x11.display))
2791     {
2792         if (!waitForEvent(&timeout))
2793             break;
2794     }
2795 
2796     _glfwPollEventsX11();
2797 }

2781 {
2782     while (!XPending(_glfw.x11.display))
2783         waitForEvent(NULL);
2784 
2785     _glfwPollEventsX11();
2786 }

2490 {
2491     Window focused;
2492     int state;
2493 
2494     XGetInputFocus(_glfw.x11.display, &focused, &state);
2495     return window->x11.handle == focused;
2496 }

2545 {
2546     Window w = _glfw.x11.root;
2547     while (w)
2548     {
2549         Window root;
2550         int rootX, rootY, childX, childY;
2551         unsigned int mask;
2552 
2553         _glfwGrabErrorHandlerX11();
2554 
2555         const Bool result = XQueryPointer(_glfw.x11.display, w,
2556                                           &root, &w, &rootX, &rootY,
2557                                           &childX, &childY, &mask);
2558 
2559         _glfwReleaseErrorHandlerX11();
2560 
2561         if (_glfw.x11.errorCode == BadWindow)
2562             w = _glfw.x11.root;
2563         else if (!result)
2564             return GLFW_FALSE;
2565         else if (w == window->x11.handle)
2566             return GLFW_TRUE;
2567     }
2568 
2569     return GLFW_FALSE;
2570 }

2499 {
2500     return getWindowState(window) == IconicState;
2501 }

2511 {
2512     Atom* states;
2513     GLFWbool maximized = GLFW_FALSE;
2514 
2515     if (!_glfw.x11.NET_WM_STATE ||
2516         !_glfw.x11.NET_WM_STATE_MAXIMIZED_VERT ||
2517         !_glfw.x11.NET_WM_STATE_MAXIMIZED_HORZ)
2518     {
2519         return maximized;
2520     }
2521 
2522     const unsigned long count =
2523         _glfwGetWindowPropertyX11(window->x11.handle,
2524                                   _glfw.x11.NET_WM_STATE,
2525                                   XA_ATOM,
2526                                   (unsigned char**) &states);
2527 
2528     for (unsigned long i = 0;  i < count;  i++)
2529     {
2530         if (states[i] == _glfw.x11.NET_WM_STATE_MAXIMIZED_VERT ||
2531             states[i] == _glfw.x11.NET_WM_STATE_MAXIMIZED_HORZ)
2532         {
2533             maximized = GLFW_TRUE;
2534             break;
2535         }
2536     }
2537 
2538     if (states)
2539         XFree(states);
2540 
2541     return maximized;
2542 }

2504 {
2505     XWindowAttributes wa;
2506     XGetWindowAttributes(_glfw.x11.display, window->x11.handle, &wa);
2507     return wa.map_state == IsViewable;
2508 }
